('20200401104355', 7, 'Bán nhà đường Sơn Nam, Khu 16ha, Rạch Giá, Kiên Giang', 'ban-nha-duong-son-nam-khu-16ha-rach-gia-kien-giang', '<p>Cần b&aacute;n gấp nh&agrave; đẹp khu 16ha!</p>\r\n\r\n<p>????L&ocirc; 16 đường Sơn Nam,</p>\r\n\r\n<p>Diện t&iacute;ch ngang 8*13.</p>\r\n\r\n<p>????&nbsp;Để lại nội thất gồm: 4p ngủ, 3 nh&agrave; vệ sinh, ph&ograve;ng bếp, ph&ograve;ng khách, s&acirc;n phơi, 01 nh&agrave; để xe &ocirc; t&ocirc;. 2 tủ &acirc;m tường, 1 giường ngủ đồng bộ tủ &acirc;m, tủ bếp, tủ rượu trang tr&iacute; v&aacute;ch ngăn ph&ograve;ng kh&aacute;ch v&agrave; bếp.</p>\r\n\r\n<p>LH: 0901020833</p>\r\n', '20200401104632EbpgwRfv3y20191123083102.jpg', '20200317152104', '20200321063924', '20180520235931', 'kiengiang', '104', 4000000000, NULL, 'Phường Vĩnh Quang, TP. Rạch Giá', NULL, 'KG', '0901020833', '', '2020-04-01 10:47:42', 1, '20200401104742', 1, 0, 23, 0, 'admin', 'bannhaduongsonnamkhu16harachgiakiengiangcanbangapnhadepkhu16halo16duongsonnamdientichngang813nbspdelainoithatgom4pngu3nhavesinhphongbepphongkhachsanphoi01nhadexeoto2tuamtuong1giuongngudongbotuamtubepturuoutrangtrivachnganphongkhachvabeplh0901020833', '2020-04-01 10:47:42', NULL),
('20200327185922', 6, 'Bán nhà đường Nguyễn Hiền Điều (Dự án nam An Hòa), Rạch Giá, Kiên Giang', 'ban-nha-duong-nguye-n-hien-dieu-du-an-nam-an-hoa-rach-gia-kien-giang', '<p>Bán nhà đường Nguy&ecirc;̃n Hi&ecirc;̀n Đi&ecirc;̀u (Dự án nam An Hòa), Rạch Giá, Ki&ecirc;n Giang<br />\r\nB&aacute;n nh&agrave; l&ocirc; A2, 1 trệt 1 lầu, đường Nguyễn hiền điều thuộc dự &aacute;n Nam An H&ograve;a<br />\r\n????&nbsp;Nh&agrave; tọa lạc tại vị tr&iacute; đắc địa, thuộc trung t&acirc;m th&agrave;nh phố, gần khu vui chơi, bệnh viện v&agrave; nhiều tiện &iacute;ch c&ocirc;ng cộng kh&aacute;c. Th&iacute;ch hơp để ở v&agrave; đầu tư sinh lời&nbsp;????????<br />\r\n????&nbsp;Nh&agrave; đ&atilde; đ&oacute;ng tiền lần 1 l&agrave; 500tr, chuẩn bị đ&oacute;ng tiền lần 2. Đủ điều kiện sang t&ecirc;n.<br />\r\nGi&aacute; b&aacute;n lại 320tr ( chưa bao gồm tiền đ&oacute;ng cho c&ocirc;ng ty)<br />\r\nLi&ecirc;n hệ 0988365716<br />\r\nC&aacute;m ơn đ&atilde; duyệt tin</p>\r\n', '20200401104345MDy0oFJbsb20191106234132.jpg', '20200317152104', '20200321063929', '20180520235931', 'kiengiang', '100', 320000000, '', '', NULL, 'KG', '0988365716', '', '2020-03-27 19:00:38', 1, '20200327190038', 1, 0, 22, 0, 'admin', '', '2020-05-07 11:08:01', NULL),
('20200401104742', 8, 'Bán nhà quốc lộ 61 - Khu bến xe tỉnh Kiên Giang', 'ban-nha-quoc-lo-61-khu-ben-xe-tinh-kien-giang', '<p>Khu vực an ninh tốt, ph&ugrave; hợp kinh doanh, cho thu&ecirc;,..<br />\r\nNh&agrave; 01 trệt + 2 lầu<br />\r\nMiễn tiếp trung gian, nh&agrave; ch&iacute;nh chủ</p>\r\n', '20200401105000tzJr5BVC87thumb_20191123083430.jpg', '20200317152104', '20200401105110', '20200320030157', 'kiengiang', '110', 4000000000, '', 'Khu dân cư bến xe tỉnh Kiên Giang, Xã Vĩnh Hòa Hiệp, Châu Thành', NULL, 'Đăng Hưng', '0979818475', '', '2020-04-01 10:50:02', 0, '20200401105002', 1, 0, 32, 0, 'admin', 'bannhaquoclo61khubenxetinhkiengiangkhuvucanninhtotphuhopkinhdoanhchothuenha01tret2laumientieptrunggiannhachinhchu', '2020-05-07 11:20:12', NULL),
('20200401111427', 9, 'Bán đất mặt tiền đường Nguyễn Huy Tưởng, Rạch Giá, Kiên Giang', 'ban-dat-mat-tien-duong-nguye-n-huy-tuong-rach-gia-kien-giang', '<p>Bán đ&acirc;́t mặt ti&ecirc;̀n đường Nguy&ecirc;̃n Huy Tưởng, Rạch Giá, Ki&ecirc;n Giang<br />\r\nMặt tiền nguyễn huy tưởng khu cầu suối. Dt 5x18. Thổ cư 100%. Gi&aacute; 710tr. Alo 0372397999</p>\r\n', '20200401111604QC6cIiVrfZthumb_20191123083430.jpg', '20200317152104', '20200321063924', '20180520235931', 'kiengiang', '90', 710000000, NULL, 'Phường Vĩnh Quang, TP. Rạch Giá', NULL, 'KG', '0372397999', '', '2020-04-01 11:16:11', 0, '20200401111611', 1, 0, 43, 0, 'admin', 'bandatmattienduongnguyenhuytuongrachgiakiengiangbandatmattienduongnguyenhuytuongrachgiakiengiangmattiennguyenhuytuongkhucausuoidt5x18thocu100gia710tralo0372397999', '2020-04-01 11:16:11', NULL),
('20200401112259', 10, 'Bán đất mặt tiền đường Lâm Quang Ky, Rạch Giá, Kiên Giang', 'ban-dat-mat-tien-duong-lam-quang-ky-rach-gia-kien-giang', '<p>Bán đ&acirc;́t mặt ti&ecirc;̀n đường L&acirc;m Quang Ky, Rạch Giá, Ki&ecirc;n Giang<br />\r\nDiện t&iacute;ch: 5 X 38 Thổ cư 110m2<br />\r\n????Giao th&ocirc;ng thuận tiện: c&aacute;ch chợ Tắc R&aacute;n-1km; Khu vui chơi Ph&uacute; Cường-1,5km; Trường-300m; Quảng trường-800m....<br />\r\n????Nằm ngay trung t&acirc;m th&agrave;nh phố, khu tấp nập<br />\r\n????Gần c&aacute;c khu tiện &iacute;ch, qu&aacute;n ăn, qu&aacute;n c&agrave; ph&ecirc;☕️, c&acirc;y xăng....<br />\r\n????Mua b&aacute;n kinh doanh, đầu tư sinh lời ạ<br />\r\n☎️☎️Vui l&ograve;ng ib hoặc li&ecirc;n hệ 082 82 82 000</p>\r\n', '202004011124537rUkHpbTNl20191124155859.jpg', '20200317152104', '20200321063929', '20180520235931', 'kiengiang', '110', 0, NULL, 'Phường An Hòa, TP. Rạch Giá', NULL, 'KG', '082 82 82 000', '', '2020-04-01 11:24:58', 0, '20200401112458', 1, 0, 40, 0, 'admin', '', '2020-04-01 11:24:58', NULL),
('20200401112459', 11, 'Bán SHOPHUOSE Đường số 9, KĐT Phú Cường, Rạch Giá, Kiên Giang', 'ban-shophuose-duong-so-9-kdt-phu-cuong-rach-gia-kien-giang', '<p>Bán SHOPHUOSE Đường s&ocirc;́ 9, KĐT Phú Cường, Rạch Giá, Ki&ecirc;n Giang<br />\r\nC&acirc;̀n ti&ecirc;̀n Thu h&ocirc;̀i v&ocirc;́n. Bán bi&ecirc;̣t thự shop house P39 Đường s&ocirc;́ 9 , Phú cường Tprg.KG.<br />\r\nK&ecirc;́t c&acirc;́u : 1 tr&ecirc;̣t 3 l&acirc;̀u. Nhà hoàn thi&ecirc;̣n 2 mặt ti&ecirc;̀n. B&ecirc;n trong th&ocirc;.<br />\r\nDt : 5 x 24m. Hướng T&acirc;y bắc.<br />\r\nH&ocirc;̃ trợ ng&acirc;n hàng.<br />\r\n&nbsp;Đường r&ocirc;̣ng 21m.<br />\r\nGiá: 4ty2 bu&ocirc;ng đu&ocirc;i.<br />\r\n093.999.83.84.</p>\r\n', '20200401112723pZu7TlwTaD20191125081952.jpg', '20200317152104', '20200321063929', '20180520235931', 'kiengiang', '120', 4, '', 'Đường số 9, KĐT Phú Cường, Rạch Giá, Kiên Giang', NULL, 'KG', '093.999.83.84', '', '2020-04-01 11:27:26', 1, '20200401112726', 1, 0, 24, 0, 'admin', 'banshophuoseduongso9kdtphucuongrachgiakiengiangbanshophuoseduongso9kdtphucuongrachgiakiengiangcantienthuhoivonbanbietthushophousep39duongso9phucuongtprgkgketcau1tret3launhahoanthien2mattienbentrongthodt5x24mhuongtaybachotronganhangnbspduongrong21mgia4ty2buongduoi0939998384', '2020-05-10 00:32:46', 1),
