INSERT INTO `sanpham` (`sp_id`, `sp_ten`, `sp_hinh`, `sp_tom_tat`, `sp_chi_tiet`, `sp_slug`, `sp_trang_thai`, `sp_thu_tu`, `sp_noi_bat`, `sp_cm_id`, `sp_nd_id`, `sp_ngay_dang`, `sp_luot_xem`, `sp_ban_chay`, `sp_khuyen_mai`, `sp_giam_gia`, `sp_moi`, `sp_gia_thi_truong`, `sp_gia_ban`, `sp_ma`, `sp_search`, `sp_ngay_cap_nhat`, `sp_thuoc_tinh1`, `sp_thuoc_tinh2`, `sp_thuoc_tinh3`, `sp_chi_tiet2`, `sp_chi_tiet3`) VALUES
('20221002170940', 'Máy xông mũi họng AlphaNeb Plus', '20221002171724a8cd040bdde96cc9456cc6949397b0b7-jpg.jpg', 'Aukewel AlphaNeb Plus là máy xông khí cao cấp được sản xuất bởi Hoa Kỳ. Được nhiều người đánh giá là sản phẩm có mẫu mã đẹp và chất lượng tốt. Sản phẩm sử dụng khí dung để điều trị một số bệnh về đường hô hấp. Máy có tốc độ phun khí cao giúp đạt hiệu quả nhanh và giảm tối thiểu về phản ứng phụ do thuốc gây nên.', '<p>Kh&iacute; dung trị liệu l&agrave; một phương ph&aacute;p điều trị bằng c&aacute;ch đưa thuốc trực tiếp v&agrave;o ni&ecirc;m mạc đường h&ocirc; hấp, do cơ chế n&agrave;y thuốc sẽ đọng lại tr&ecirc;n ni&ecirc;m mạc đường h&ocirc; hấp, n&ecirc;n liệu ph&aacute;p x&ocirc;ng kh&iacute; dung chỉ cần d&ugrave;ng liều thuốc thấp, thời gian đạt hiệu quả nhanh v&agrave; giảm tối thiểu về phản ứng phụ do thuốc g&acirc;y n&ecirc;n.</p>\r\n\r\n<p>- Tốc độ phun kh&iacute; cao bảo đảm thời gian điều trị</p>\r\n\r\n<p>- Dể sử dụng v&agrave; bảo quản</p>\r\n\r\n<p><strong>M&aacute;y x&ocirc;ng kh&iacute; dung AlphaNeb Plus&nbsp;</strong><strong>l&yacute; tưởng để ph&ograve;ng v&agrave; điều trị c&aacute;c bệnh l&yacute; sau:</strong></p>\r\n\r\n<p>- Vi&ecirc;m xoang</p>\r\n\r\n<p>- Vi&ecirc;m mũi</p>\r\n\r\n<p>- Hen suyễn</p>\r\n\r\n<p>- Dị ứng v&agrave; rối loạn đường h&ocirc; hấp.</p>\r\n\r\n<p><strong>M&aacute;y x&ocirc;ng kh&iacute; dung AlphaNeb Plus</strong><strong>&nbsp;k&egrave;m theo phụ kiện:</strong></p>\r\n\r\n<p>+ 01 Bầu kh&iacute; dung</p>\r\n\r\n<p>+ 01 D&acirc;y dẫn kh&iacute;</p>\r\n\r\n<p>+ 01 Mặt nạ ng lớn</p>\r\n\r\n<p>+ 01 Mặt nạ trẻ em</p>\r\n\r\n<p>+ 01 Ống x&ocirc;ng mũi</p>\r\n\r\n<p>+ 01 Ống x&ocirc;ng họng</p>\r\n\r\n<p>+ 05 Lọc kh&iacute;</p>\r\n\r\n<p><strong>Sản phẩm bảo h&agrave;nh 24 th&aacute;ng&nbsp;</strong></p>\r\n', 'may-xong-mui-hong-alphaneb-plus', 1, '20221002171724', 1, '20220604161323', 'admin', '2022-10-02 17:17:24', 1094, 1, 1, 34, 1, 990000, 650000, '', 'mayxongmuihongalphanebplus', '2022-10-02 17:17:24', '', '', '', '', ''),
('20221002172407', 'Máy tạo oxy Yuwell 7F-3EW', '20221002173750screenshot_20210818-100344_chrome-jpg.jpg', 'Máy tạo oxy Yuwell 7F-3EW  3lit/phút là một thiết bị có chức năng rất quan trọng trong việc điều trị, hỗ trợ người già, bệnh nhân trong chức năng hô hấp. ', '<p><span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">Sản phẩm n&agrave;y chuy&ecirc;n d&ugrave;ng cho những ai gặp phải kh&oacute; khăn về h&ocirc; hấp v&agrave; cần được cung cấp lượng oxy ở mức độ nhẹ. Tại c&aacute;c trung t&acirc;m y tế, 7F-3EW được xem như một trợ thủ đắc lực trong việc hỗ trợ điều trị nhiều loại bệnh l&yacute; về tim mạch, phế quản, phổi,... Ngo&agrave;i ra, nhiều người bệnh được b&aacute;c sĩ chỉ định n&ecirc;n sử dụng d&ograve;ng m&aacute;y tạo oxy n&agrave;y tại nh&agrave;.</span></p>\r\n\r\n<p><span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">M&aacute;y tạo oxy Yuwell 7F-3EW c&oacute; dung t&iacute;ch 3 l&iacute;t, lưu lượng kh&iacute; thở c&oacute; thể cung cấp từ 1 - 3 l&iacute;t/ph&uacute;t. Độ tinh khiết của d&ograve;ng chảy oxy đạt tới hơn 93% v&agrave; bạn c&oacute; thể sử dụng ch&uacute;ng ổn định trong nhiều giờ m&agrave; kh&ocirc;ng c&oacute; bất kỳ sự thay đổi n&agrave;o.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">Ở phi&ecirc;n bản n&agrave;y của h&atilde;ng Yuwell, m&aacute;y tạo oxy sẽ lấy kh&ocirc;ng kh&iacute; từ ph&iacute;a trước của m&aacute;y n&ecirc;n sẽ gi&uacute;p nồng độ kh&ocirc;ng kh&iacute; đi v&agrave;o m&aacute;y sạch hơn v&agrave; tiết kiệm điện hơn rất nhiều. Đ&acirc;y cũng ch&iacute;nh l&agrave; ưu điểm nổi bật đ&atilde; được khắc phục v&agrave; cải tiến ở 7F-3EW.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Kết hợp chức năng x&ocirc;ng kh&iacute; dung trực tiếp</h3>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">M&aacute;y tạo oxy kết hợp x&ocirc;ng kh&iacute; dung l&agrave; d&ograve;ng sản phẩm được nhiều người t&igrave;m kiếm. Hiện nay đ&acirc;y cũng l&agrave; một trong số những ti&ecirc;u ch&iacute; được nhiều người đặt ra khi lựa chọn d&ograve;ng sản phẩm n&agrave;y. M&aacute;y tạo oxy Yuwell 7F-3EW c&oacute; chế độ x&ocirc;ng kh&iacute; dung hoạt động hiệu quả tương đương với d&ograve;ng m&aacute;y chuy&ecirc;n dụng v&agrave; c&ograve;n c&oacute; chế độ hẹn giờ l&ecirc;n tới 12 giờ đồng hồ.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">Để chuyển từ thở oxy sang chế độ x&ocirc;ng kh&iacute; dung bạn chỉ cần thực hiện một v&agrave;i thao t&aacute;c cơ bản tr&ecirc;n m&aacute;y. Ngo&agrave;i ra, khi mua h&agrave;ng m&aacute;y sẽ đi k&egrave;m với bộ phụ kiện bao gồm mặt nạ x&ocirc;ng v&agrave; ống x&ocirc;ng gi&uacute;p bạn thoải m&aacute;i lựa chọn sản phẩm ph&ugrave; hợp nhất.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thiết kế hiện đại, dễ d&agrave;ng di chuyển</h3>\r\n\r\n<p><br />\r\n<span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">M&aacute;y tạo oxy Yuwell 7F-3EW c&oacute; thiết kế nhỏ gọn, thẩm mỹ v&agrave; gi&uacute;p người d&ugrave;ng dễ d&agrave;ng sử dụng. Tổng c&acirc;n nặng của m&aacute;y khoảng 22kg v&agrave; c&ograve;n được kết hợp th&ecirc;m 4 b&aacute;nh xe lăn gi&uacute;p việc di chuyển m&aacute;y trở n&ecirc;n dễ d&agrave;ng hơn.</span><br />\r\n<br />\r\n<span style=\"background-color:rgb(241, 241, 241); color:rgb(70, 70, 70); font-family:roboto,sans-serif; font-size:16px\">Tr&ecirc;n bảng điều khiển cảu m&aacute;y c&oacute; phần cảnh b&aacute;o an to&agrave;n những trường hợp như: mất điện, cảnh b&aacute;o &aacute;p suất thấp, cảnh b&aacute;o sự cố m&aacute;y n&eacute;n kh&iacute;; cảnh b&aacute;o tắc d&acirc;y dẫn kh&iacute;,... chế độ th&ocirc;ng minh n&agrave;y khiến người d&ugrave;ng lu&ocirc;n cảm thấy an to&agrave;n v&agrave; h&agrave;i l&ograve;ng.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'may-tao-oxy-yuwell-7f-3ew', 1, '20221002173750', 1, '20221002172226', 'admin', '2022-10-02 17:37:50', 1089, 0, 0, 0, 0, 0, 0, '', 'maytaooxyyuwell7f3ew', '2022-11-13 09:38:07', '', '', '', '', ''),
('20221112141833', 'Nệm hơi chống loét AIR DOCTOR (FU‑AM002)', '20221112142917air-doctor-2-jpg.jpg', 'Người già yếu do nằm lâu một chỗ nên vùng da ở mông, lưng dễ bị hầm, là môi trường thuận lợi cho vi khuẩn kí sinh trùng phát triển. Đối với người tai biến nằm một chỗ, sự tuần hoàn máu yếu, nhất là ở vùng da ít cử động và đè nặng, da sẽ bị hoại tử và không có khả năng tự làm lành như da bình thường dẫn đến tình trạng lở loét gây đau đớn và khó chịu cho người bệnh cũng như khó khăn trong việc giúp người bệnh vệ sinh.', '<p><span style=\"color:rgba(0, 0, 0, 0.8); font-family:helvetica neue,helvetica,arial,文泉驛正黑,wenquanyi zen hei,hiragino sans gb,儷黑 pro,lihei pro,heiti tc,微軟正黑體,microsoft jhenghei ui,microsoft jhenghei,sans-serif; font-size:14px\">Chất liệu tốt &ndash; Cấu tạo đặc biệt Nệm chống lo&eacute;t AIR DOC TOR được bơm hơi theo c&ocirc;ng nghệ H&agrave;n quốc, nệm được l&agrave;m bằng chất liệu PVC y tế nguy&ecirc;n chất, khả năng chịu lực đ&egrave; nặng l&ecirc;n đến 150kg. </span></p>\r\n\r\n<p><span style=\"color:rgba(0, 0, 0, 0.8); font-family:helvetica neue,helvetica,arial,文泉驛正黑,wenquanyi zen hei,hiragino sans gb,儷黑 pro,lihei pro,heiti tc,微軟正黑體,microsoft jhenghei ui,microsoft jhenghei,sans-serif; font-size:14px\">Sản phẩm c&oacute; cấu tạo nhiều m&uacute;i theo h&igrave;nh mắt c&aacute;o xen kẽ nhau, khi bơm hơi sẽ được lu&acirc;n chuyển li&ecirc;n tục từ m&uacute;i hơi n&agrave;y sang m&uacute;i hơi kh&aacute;c tạo cảm gi&aacute;c &ecirc;m tho&aacute;ng, gi&uacute;p giảm tối đa lực tiếp x&uacute;c l&ecirc;n bề mặt da, tr&aacute;nh lở lo&eacute;t cho người bệnh phải nằm l&acirc;u ng&agrave;y, gi&uacute;p giảm đau nhức khi người bệnh kh&ocirc;ng muốn vết đau tiếp x&uacute;c trực tiếp với giường chiếu. Nệm chống lo&eacute;t AIR DOCTOR c&oacute; m&aacute;y bơm c&oacute; vỏ bằng nhựa do đ&oacute; rất bền, thiết kế gọn nhẹ. </span></p>\r\n\r\n<p><span style=\"color:rgba(0, 0, 0, 0.8); font-family:helvetica neue,helvetica,arial,文泉驛正黑,wenquanyi zen hei,hiragino sans gb,儷黑 pro,lihei pro,heiti tc,微軟正黑體,microsoft jhenghei ui,microsoft jhenghei,sans-serif; font-size:14px\">Gắn m&aacute;y bơm th&ocirc;ng qua đoạn d&acirc;y nối với nệm. Bật nguồn m&aacute;y bơm, l&uacute;c n&agrave;y m&aacute;y sẽ từ từ bơm hơi v&agrave;o trong đệm. Khoảng 30 ph&uacute;t đệm sẽ đầy hơi. Lu&ocirc;n giữ m&aacute;y bơm khi sử dụng, với động cơ khỏe, lượng hơi bơm mạnh v&agrave; đều theo c&aacute;ch c&agrave;i đặt thời gian bơm xả trong v&ograve;ng 4 - 8 ph&uacute;t chu kỳ sẽ lặp lại 1 lần, c&oacute; thể điều chỉnh mức độ bơm hơi ph&ugrave; hợp. </span></p>\r\n\r\n<p><span style=\"color:rgba(0, 0, 0, 0.8); font-family:helvetica neue,helvetica,arial,文泉驛正黑,wenquanyi zen hei,hiragino sans gb,儷黑 pro,lihei pro,heiti tc,微軟正黑體,microsoft jhenghei ui,microsoft jhenghei,sans-serif; font-size:14px\">Thiết kế nhỏ gọn &ndash; dễ sử dụng Dễ d&agrave;ng sử dụng khi cần sử dụng chỉ cần tự đặt nệm ở tr&ecirc;n b&agrave;n, bề mặt phẳng hoặc để một đầu l&ecirc;n lang can giường sao cho nệm được trải phẳng, kh&ocirc;ng c&ograve;n nếp gấp n&agrave;o l&agrave; c&oacute; thể sử dụng ngay.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Sản phẩm bảo h&agrave;nh 12 th&aacute;ng</span></p>\r\n', 'nem-hoi-chong-loet-air-doctor-fu-am002', 1, '20221112142917', 1, '20220612215448', 'admin', '2022-11-12 14:29:17', 878, 1, 1, 17, 1, 899000, 750000, '', 'nemhoichongloetairdoctorfuam002', '2022-11-12 14:41:41', '', '', '', '', ''),
('20221112143054', 'Nệm hơi chống loét ONECARE BM-116', '20221112144102bm-116-a-jpg.jpg', 'Nệm hơi chống loét OneCare BM-116 với công nghệ chống rung, giảm ồn cho máy bơm và nệm PVC Nhật Bản đạt các tiêu chuẩn quốc tế: ISO 13485/9001, CE, CB, RoHS, FDA, C(UL)US.', '<p><span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nệm hơi chống lo&eacute;t OneCare BM-116 với c&ocirc;ng nghệ chống rung, giảm ồn cho m&aacute;y bơm v&agrave; nệm PVC Nhật Bản đạt c&aacute;c ti&ecirc;u chuẩn quốc tế: ISO 13485/9001, CE, CB, RoHS, FDA, C(UL)US.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nệm hơi chống lo&eacute;t gồm 2 phần: M&aacute;y bơm tuần ho&agrave;n v&agrave; nệm hơi PVC.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">M&aacute;y bơm nhỏ gọn, &ecirc;m, tiết kiệm điệm năng với n&uacute;t xoay chỉnh &aacute;p lực (độ cứng của nệm) đơn giản. M&aacute;y bơm tự động thay đổi chu kỳ tuần ho&agrave;n bơm/xả cho 2 đường chứa hơi nhằm li&ecirc;n tục thay đổi lưc tỳ đ&egrave; l&ecirc;n bề mặt da của bệnh nh&acirc;n gi&uacute;p th&ocirc;ng tho&aacute;ng.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nệm hơi PVC dạng m&uacute;i bong b&oacute;ng (130 m&uacute;i), chịu &aacute;p lực cao với 2 đường dẫn hơi n&eacute;n ri&ecirc;ng biệt.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">TH&Ocirc;NG SỐ KĨ THUẬT</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">K&iacute;ch thước</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nệm hơi: 200 x 90 x 7 cm.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">M&eacute;p gấp: 50cm cho 2 b&ecirc;n.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">M&aacute;y bơm: 27 x 13 x 10.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">K&iacute;ch thước bao b&igrave;: 31x28x11 cm</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Trọng lượng:</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nệm hơi: 2.3 Kg</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">M&aacute;y bơm: 1.4kg.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Trọng lượng tối đa của bệnh nh&acirc;n: 135 kg.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Nguồn điện: 220V 50Hz 0.1A Max.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">NGUY&Ecirc;N TẮC HOẠT ĐỘNG</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Lu&acirc;n chuyển &aacute;p lực theo nhịp vừa đủ nhằm tr&aacute;ch hiện tượng n&eacute;n mạch m&aacute;u k&eacute;o d&agrave;i g&acirc;y ra t&igrave;nh trạng thiếu oxy cho m&ocirc;.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Lu&acirc;n chuyển &aacute;p lực giữa c&aacute;c m&uacute;i nệm đến vị tr&iacute; của bệnh nh&acirc;n.</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">C&Aacute;CH SỬ DỤNG</span><br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Cắm điện li&ecirc;n tục, m&aacute;y sẽ bơm hơi v&agrave;o đệm, do d&ograve;ng kh&iacute; lu&acirc;n chuyển từ vị tr&iacute; n&agrave;y đến vị tr&iacute; kh&aacute;c n&ecirc;n đệm như 1 m&aacute;y matxa v&agrave; chống lo&eacute;t, ngăn ngừa lo&eacute;t th&ecirc;m</span><br />\r\n<br />\r\n<span style=\"color:rgb(0, 0, 0); font-family:sans-serif; font-size:16px\">Sản phẩm bảo h&agrave;nh 12 th&aacute;ng</span></p>\r\n', 'nem-hoi-chong-loet-onecare-bm-116', 1, '20221112144102', 1, '20220612215448', 'admin', '2022-11-12 14:41:02', 894, 1, 1, 15, 1, 1000000, 850000, '', 'nemhoichongloetonecarebm116', '2022-11-12 14:41:02', '', '', '', '', ''),
('20221112144143', 'Nệm hơi chống loét Dolphin DN-300 (Đài Loan)', '202211121516570zst80_simg_b5529c_250x250_maxb-png.png', 'DN 300Plus giúp cơ thể thông thoáng, chống loét do nằm lâu ngày hoặc bị hầm hơi do phần lưng tiếp xúc với giường gây nóng và tạo vết loét. Nệm sử dụng nguyên lý bơm đảo chiều liên tục (3 phút đảo chiều 1 lần) giữa các hàng múi xen kẽ với nhau giãm áp lực đè nén của cơ thể của người bệnh xuống vị trí nệm.', '<p><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">Model:&nbsp;<strong>DN-300 Plus</strong></span></p>\r\n\r\n<p><span style=\"color:rgb(51, 51, 51); font-family:arial,sans-serif; font-size:10pt\">Hiệu:&nbsp;<strong>Dolphin</strong><br />\r\n<br />\r\nXuất xứ: Made in Taiwan<br />\r\n<br />\r\nH&agrave;ng chất lượng cao - Best quality<br />\r\n<br />\r\nGiấy ph&eacute;p lưu h&agrave;nh: tại Mỹ: FDA, tại Chậu Au: CE<br />\r\n<br />\r\nChứng chỉ chất lượng: ISO9001, ISO13485, GMP, UL</span></p>\r\n\r\n<p>- Nệm chống lo&eacute;t l&agrave; loại nệm gi&uacute;p người bệnh nằm m&aacute;t th&ocirc;ng tho&aacute;ng kh&ocirc;ng g&acirc;y ra c&aacute;c vết lo&eacute;t.</p>\r\n\r\n<p>- Nệm c&oacute; thể để chế độ căng hơi hoặc giảm xuống mềm để đỡ đau lưng người bệnh.</p>\r\n\r\n<p>- Nệm chống lo&eacute;t Dolphin gi&uacute;p cơ thể th&ocirc;ng tho&aacute;ng, chống lo&eacute;t do nằm l&acirc;u ng&agrave;y hoặc bị hầm hơi do phần lưng tiếp x&uacute;c với giường g&acirc;y n&oacute;ng v&agrave; tạo vết lo&eacute;t. Nhờ nguy&ecirc;n tắc đảo chiều kh&iacute; li&ecirc;n tục n&agrave;y, c&aacute;c phần m&ocirc; v&agrave; da của cơ thể sẽ c&oacute; khoảng trống nhất định gi&uacute;p cho kh&ocirc;ng kh&iacute; b&ecirc;n ngo&agrave;i lưu th&ocirc;ng dưới phần lưng. Đồng thời c&aacute;c luồng kh&iacute; trong nệm lu&ocirc;n lưu th&ocirc;ng v&agrave; nhiệt độ lu&ocirc;n duy tr&igrave; ở khoảng 27 - 28 &deg;C l&agrave;m m&aacute;t lưng v&agrave; c&aacute;c phần kh&aacute;c tiếp x&uacute;c với mặt nệm.</p>\r\n\r\n<p>- Chất liệu nệm bằng nguy&ecirc;n liệu nhựa PVC trung t&iacute;nh an to&agrave;n chuy&ecirc;n d&ugrave;ng trong y tế với cấu tạo từng m&uacute;i khi bơm hơi sẽ căn phồng l&ecirc;n tạo th&ocirc;ng tho&aacute;ng so với nằm tr&ecirc;n nệm thường dễ bị hầm hơi v&agrave; g&acirc;y lo&eacute;t cục bộ.</p>\r\n\r\n<p>- M&aacute;y bơm của nệm sử dụng điện 220V - 50Hz với cục biến điện ở trong th&acirc;n m&aacute;y để đưa d&ograve;ng điện v&agrave;o motor chỉ c&ograve;n 24V. Hơn nữa, phần nệm nối với m&aacute;y b&agrave;ng một ống d&acirc;y đ&ocirc;i c&oacute; chiều d&agrave;i hơn 1 m&eacute;t cho n&ecirc;n nệm rất an to&agrave;n cho người sử dụng. nệm chống lo&eacute;t dolphin dn 300plus</p>\r\n\r\n<p>T&iacute;nh năng nổi bật l&agrave;m m&aacute;t bề mặt tiếp x&uacute;c giữa lưng - nệm v&agrave; lu&ocirc;n duy tr&igrave; nhiệt độ vừa phải. Tr&aacute;nh khả năng g&acirc;y lo&eacute;t ngay xương cụt v&agrave; xương chậu Van bơm đảo chiều li&ecirc;n tục gi&uacute;p cơ thể người bệnh lu&ocirc;n thay đổi phần da tiếp x&uacute;c với mặt nệm. Tạo cảm gi&aacute;c thư gi&atilde;n v&agrave; &ecirc;m, gi&uacute;p người bệnh nằm dưỡng bệnh c&oacute; giấc ngủ s&acirc;u hơn. Đặc biệt, nệm rất th&iacute;ch hợp cho người gi&agrave;, người nằm liệt giường l&acirc;u năm, người bị tai biến, người nằm dưỡng thương l&acirc;u ng&agrave;y,..</p>\r\n\r\n<p>Th&ocirc;ng số kỹ thuật : Sử dụng nguy&ecirc;n l&yacute; bơm đảo chiều li&ecirc;n tục (3 ph&uacute;t đảo chiều 1 lần).</p>\r\n\r\n<p>Chất liệu nệm bằng nguy&ecirc;n liệu nhựa PVC trung t&iacute;nh an to&agrave;n. M&aacute;y bơm sử dụng điện 220V - 50Hz.</p>\r\n\r\n<p>Bảo h&agrave;nh : 2 năm</p>\r\n', 'nem-hoi-chong-loet-dolphin-dn-300-dai-loan', 1, '20221112151657', 1, '20220612215448', 'admin', '2022-11-12 15:16:57', 902, 1, 1, 14, 1, 2200000, 1900000, '', 'nemhoichongloetdolphindn300dailoan', '2022-11-12 16:03:01', '', '', '', '', ''),
